import { nothing } from "lit";
/**
 * if value is truthy, return it, otherwise return nothing
 */
export declare function cond(value: any): typeof value | typeof nothing;
/**
 * if condition is truthy, return trueCase, otherwise return nothing
 */
export declare function cond(condition: unknown, trueCase: any): typeof trueCase | typeof nothing;
/**
 * condition is true, so always return trueCase
 */
export declare function cond(condition: true, trueCase: any): typeof trueCase;
/**
 * condition is false, to will always return nothing
 */
export declare function cond(condition: false, trueCase: any): typeof nothing;
/**
 * if condition is truthy, return trueCase, otherwise return falseCase
 */
export declare function cond(condition: unknown, trueCase: any, falseCase: any): typeof trueCase | typeof falseCase;
/**
 * condition is true, so always return trueCase
 */
export declare function cond(condition: true, trueCase: any, falseCase: any): typeof trueCase;
/**
 * condition is false, so always return falseCase
 */
export declare function cond(condition: false, trueCase: any, falseCase: any): typeof falseCase;
